/* ================================= */
/* CSS for control sap.m/IconTabBar  */
/* Base theme                        */
/* ================================= */
/* ------------------------------------------------ */
/* IconTabBar main class                            */
/* ------------------------------------------------ */
:host(ui5-tab-container) {
  display: block;
  width: 100%;
}
ui5-tab-container {
  display: block;
  font-family: "72", Arial, Helvetica, sans-serif;
  font-size: 1rem;
}
.sapMITB {
  display: block;
  position: relative;
  width: 100%;
}
.sapMITB .sapMITBItemNoCount.sapMITBFilter:last-child {
  padding-right: 0.8rem;
}
.sapMITB.sapMITBNoContentPadding > .sapMITBContainerContent > .sapMITBContent {
  padding: 0;
}
.sapMITB .sapMITBArrowScrollLeft {
  left: 0.25rem;
  padding: 1rem 0.4rem;
}
.sapMITB .sapMITBArrowScrollRight {
  padding: 1rem 0.4rem;
  right: 0.25rem;
}
.sapMITB .sapMITH .sapMITHBtn {
  padding-right: 0.25rem;
}
.sapMITB .sapMITBScrollContainer {
  margin: 0 1.75rem;
}
.sapMITB .sapMITHOverflowList .sapMITBScrollContainer {
  margin: 0 4.25rem 0 1.75rem;
}
.sapMITB .sapMITHOverflowList .sapMITBArrowScrollRight {
  right: 2.75rem;
}
.sapMITB .sapMITBArrowScrollLeftTextOnly {
  left: 0.25rem;
  padding: 1.0625rem 0.4rem;
}
.sapMITB .sapMITBArrowScrollRightTextOnly {
  padding: 1.0625rem 0.4rem;
  right: 0.25rem;
}
.sapMITB .sapMITHOverflowList .sapMITBArrowScrollRightTextOnly {
  right: 2.75rem;
}
.sapMITB .sapMITH .sapMITHBtn.sapMBtnTextOnly {
  top: 1.25rem;
}
/* Dragged Filter Item */
.sapMITBItem.sapUiDnDDragging {
  background: #eff4f9;
}
/* ------------------------------------------------ */
/* IconTabBar tab class                             */
/* ------------------------------------------------ */
.sapMITBTab {
  width: 5rem;
  display: block;
}
/* ------------------------------------------------ */
/* IconTabBar content container                     */
/* ------------------------------------------------ */
.sapMITBContent {
  padding: 1rem;
  position: relative;
  background-color: #ffffff;
}
.sapMITBContentClosed {
  background-color: transparent;
  border-bottom: none;
  margin-top: 0.750rem;
  padding: 0.125rem 0 0 0;
  position: relative;
}
.sapMITBContainerContent {
  margin-top: 0;
  padding: 0;
  position: relative;
}
.sapMITBContainerContent.sapMITBContentClosed {
  background-color: transparent;
  border-bottom: none;
}
/* ------------------------------------------------ */
/* IconTabBar filter class                          */
/* ------------------------------------------------ */
.sapMITBFilter {
  padding: 0 0.25rem;
}
.sapMITBFilter:not(.sapMITBDisabled),
.sapMITBFilter:not(.sapMITBDisabled) .sapMITBFilterIcon {
  cursor: pointer;
}
.sapMITBFilter.sapMITBFilterHidden {
  visibility: hidden;
}
.sapMITBFilter:first-child {
  padding-left: 0;
}
.sapMITBFilter:last-child {
  padding-right: 1rem;
}
.sapMITBFilter .sapMITBText {
  line-height: 0.9375rem;
  overflow: hidden;
  padding: 0.375rem 0 0;
  position: relative;
  text-overflow: ellipsis;
  width: 5rem;
  color: #333333;
  text-shadow: 0 0 0.125rem #fff;
}
.sapMITBFilter .sapMITBCount {
  color: #333333;
  text-shadow: 0 0 0.125rem #fff;
  max-width: 2rem;
  overflow: hidden;
  position: absolute;
  text-overflow: ellipsis;
}
.sapMITBFilter .sapMITBTab,
.sapMITBFilter .sapMITBCount,
.sapMITBFilter .sapMITBHorizontalWrapper,
.sapMITBFilter .sapMITBContentArrow,
.sapMITBFilter .sapMITBText {
  pointer-events: none;
}
/* ------------------------------------------------ */
/* IconTabBar Tab Filter Show All Item class        */
/* ------------------------------------------------ */
.sapMITBAll {
  margin-right: 1.25rem;
}
.sapMITBAll:not(.sapMITBDisabled) {
  cursor: pointer;
}
.sapMITBAll:first-Child > .sapMITBTab > .sapMITBCount {
  padding-left: 0;
}
.sapMITBAll > .sapMITBTab {
  height: 3rem;
  margin-bottom: 1rem;
}
.sapMITBAll .sapMITBTab {
  display: inline-block;
  height: 3rem;
  line-height: 3rem;
  width: auto;
}
.sapMITBAll .sapMITBCount {
  display: inline-block;
  font-size: 1.5rem;
  padding: 0 0.5rem;
  vertical-align: middle;
  width: auto;
  color: #333333;
  text-shadow: 0 0 0.125rem #fff;
}
.sapMITBAll .sapMITBText {
  display: inline-block;
  max-height: 3rem;
  max-width: 4rem;
  overflow: hidden;
  text-align: left;
  text-overflow: ellipsis;
  vertical-align: middle;
  white-space: normal;
  width: auto;
  color: #333333;
  text-shadow: 0 0 0.125rem #fff;
}
/* ------------------------------------------------ */
/* IconTabBar Tab Text Only class                   */
/* ------------------------------------------------ */
.sapMITBTextOnly .sapMITBFilter {
  padding: 0 1rem;
}
.sapMITBTextOnly .sapMITBFilter:first-child {
  padding-left: 0;
}
.sapMITBTextOnly .sapMITBFilter:not(.sapMITBDisabled) .sapMITBText {
  cursor: pointer;
}
.sapMITBTextOnly .sapMITBFilter .sapMITBText {
  line-height: 1rem;
  margin: 0;
  padding: 0;
  text-align: left;
  width: auto;
  overflow: visible;
}
.sapMITBTextOnly .sapMITBFilter .sapMITBCount {
  font-size: 0.875rem;
  max-width: none;
  position: relative;
}
.sapMITBTextOnly .sapMITBTab {
  height: 1rem;
  padding: 0.5625rem 0 0.3125rem 0;
  text-align: left;
  width: auto;
}
.sapMITBTextOnly > .sapMITBVertical {
  height: 3.5rem;
}
.sapMITBTextOnly > .sapMITBVertical.sapMITBFilter > .sapMITBText + .sapMITBContentArrow {
  height: 0.625rem;
}
.sapMITBTextOnly .sapMITBText {
  font-size: 0.875rem;
}
.sapMITBTextOnly .sapMITBFilterDefault > .sapMITBText {
  color: #333333;
  text-shadow: 0 0 0.125rem #fff;
}
/* ------------------------------------------------ */
/* IconTabBar Tab With No Text class                */
/* ------------------------------------------------ */
.sapMITBNoText > .sapMITBFilter > .sapMITBContentArrow {
  margin: 0 auto;
  width: 3rem;
}
.sapMITBNoText > .sapMITBFilter:first-child > .sapMITBContentArrow {
  margin: 0 auto 0 0;
}
.sapMITBNoText > .sapMITBVertical {
  height: 4rem;
}
.sapMITBNoText .sapMITBFilter:last-child {
  padding-right: 1rem;
}
.sapMITBNoText .sapMITBFilter > .sapMITBTab {
  margin: 0 0.75rem;
  width: 3rem;
}
.sapMITBNoText .sapMITBFilter:first-child > .sapMITBTab {
  margin-left: 0;
}
.sapMITBNoText .sapMITBItemNoCount.sapMITBFilter:last-child {
  padding-right: 0;
  margin-right: 0;
}
.sapMITBNoText .sapMITBItemNoCount.sapMITBFilter:last-child > .sapMITBTab {
  margin-right: 0;
}
.sapMITBNoText .sapMITBItemNoCount.sapMITBFilter:last-child .sapMITBContentArrow {
  margin-right: 0;
}
/* ------------------------------------------------ */
/* IconTabBar Stretch content height class          */
/* ------------------------------------------------ */
.sapMITBStretch {
  display: -webkit-flex;
  display: flex;
  flex-direction: column;
  -webkit-flex-direction: column;
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
}
.sapMITBStretch > .sapMITBContainerContent {
  -webkit-flex-grow: 1;
  flex-grow: 1;
}
.sapMITBStretch > .sapMITBContainerContent > .sapMITBContent {
  bottom: 0;
  left: 0;
  position: absolute;
  right: 0;
  top: 0;
}
/* ------------------------------------------------ */
/* IconTabBar Head container class                  */
/* ------------------------------------------------ */
.sapMITBHead {
  position: relative;
  white-space: nowrap;
  display: inline-block;
  margin: 1rem 0.75rem 0 0.25rem;
  padding: 0 1px;
  vertical-align: top;
  font-size: 0.75rem;
}
/* ------------------------------------------------ */
/* IconTabBar Content arrow class                   */
/* ------------------------------------------------ */
.sapMITBContentArrow {
  border-bottom: 0.125rem solid transparent;
  box-sizing: border-box;
  height: 1rem;
}
/* ------------------------------------------------ */
/* IconTabBar Tab item  class                       */
/* ------------------------------------------------ */
.sapMITBItem {
  display: inline-block;
  position: relative;
  text-align: center;
  vertical-align: top;
  color: #333333;
  font-family: "72", Arial, Helvetica, sans-serif;
}
.sapMITBDisabled {
  opacity: 0.5;
}
/* ------------------------------------------------ */
/* Vertical Tab Filter Item                         */
/* ------------------------------------------------ */
.sapMITBVertical {
  height: 5rem;
}
.sapMITBVertical.sapMITBFilter > .sapMITBText + .sapMITBContentArrow {
  height: 0.6875rem;
}
/* ------------------------------------------------ */
/* Horizontal Tab Filter Item                       */
/* ------------------------------------------------ */
.sapMITBHorizontalWrapper {
  display: inline-block;
  height: 3rem;
  left: -1rem;
  max-width: 7.5rem;
  overflow: hidden;
  text-overflow: ellipsis;
  vertical-align: top;
  width: auto;
}
.sapMITBHorizontal.sapMITBFilter > .sapMITBContentArrow {
  width: 3rem;
}
.sapMITBHorizontal.sapMITBAll > .sapMITBTab {
  margin-bottom: 0;
}
.sapMITBHorizontal.sapMITBFilter:first-child > .sapMITBTab {
  padding-left: 0;
}
.sapMITBHorizontal.sapMITBFilter:first-child > .sapMITBText {
  padding-left: 3.25rem;
}
.sapMITBHorizontal.sapMITBFilter .sapMITBText {
  padding: 0.625rem 0 0;
}
.sapMITBHorizontal.sapMITBFilter > .sapMITBHorizontalWrapper > .sapMITBText {
  display: block;
  max-width: 7.5rem;
  padding-left: 0.25rem;
  text-align: left;
  top: 0.3125rem;
  width: auto;
}
.sapMITBHorizontal.sapMITBFilter {
  height: 4rem;
}
.sapMITBHorizontal.sapMITBFilter > .sapMITBHorizontalWrapper > .sapMITBCount {
  display: block;
  max-width: 7.5rem;
  padding-left: 0.25rem;
  position: relative;
  text-align: left;
  top: 0.4375rem;
}
.sapMITBHorizontal.sapMITBFilter > .sapMITBTab {
  display: inline-block;
  height: 3rem;
  padding: 0;
  vertical-align: top;
  width: 3rem;
}
.sapMITBHorizontal + .sapMITBSep {
  width: auto;
}
.sapMITBHorizontal + .sapMITBSep > .sapMITBSepIcon {
  padding: 0 0.5rem;
}
/* ------------------------------------------------ */
/* Tab Filter In Line                               */
/* ------------------------------------------------ */
.sapMITBInLine .sapMITBFilter > .sapMITBText {
  margin: 1rem 0 0.1875rem 0;
}
.sapMITBInLine.sapMITBTextOnly > .sapMITBVertical {
  height: 3rem;
}
.sapMITBInLine.sapMITBTextOnly > .sapMITBVertical.sapMITBFilter > .sapMITBText + .sapMITBContentArrow {
  height: 0.8125rem;
}
/* ------------------------------------------------ */
/* Tab Filter Icon                                  */
/* ------------------------------------------------ */
.sapMITH .sapMITBFilterIcon {
  position: relative;
  font-family: "sap-icons";
  border-bottom-left-radius: 3rem;
  border-bottom-right-radius: 3rem;
  -moz-border-radius: 3rem;
  border-top-left-radius: 3rem;
  /* some native browsers like Samsung Galaxy S4 with Android 4.2 does not support the "border-
	radius" shorthand property but does support the long-hand properties for each corner like
	"border-top-left-radius" */
  border-top-right-radius: 3rem;
  box-sizing: border-box;
  display: inline-block;
  font-size: 1.5rem;
  height: 3rem;
  line-height: 2.75rem;
  overflow: hidden;
  width: 3rem;
  -moz-user-select: none;
  -ms-user-select: none;
  -webkit-user-select: none;
}
.sapMITBFilterNoIcon {
  display: inline-block;
  height: 3rem;
  vertical-align: middle;
  width: 3rem;
}
.sapMITBCount {
  color: #333333;
}
/* ------------------------------------------------ */
/* IconTabBar Tab Separator Item                    */
/* ------------------------------------------------ */
.sapMITBSep {
  width: 1.5rem;
  pointer-events: none;
}
.sapMITBSepLine {
  height: 3rem;
  margin: 0 0.25rem;
  width: 0;
  border: 1px solid #e5e5e5;
}
.sapMITBSepIcon {
  font-size: 1rem;
  height: 3rem;
  line-height: 3rem;
  padding: 0 0.25rem;
  color: #878787;
}
.sapMITBSepIcon::before {
  text-shadow: 0 0 0.125rem #fff;
}
.sapMITBTextUpperCase .sapMITBText {
  text-transform: uppercase;
}
.sapMITBBackgroundDesignTransparent > .sapMITBContainerContent > .sapMITBContent {
  background-color: transparent;
}
/* fix rendering issue in BlackBerry - CSN: 0120061532 0001463943 2014 */
.sap-bb .sapMITBContainerContent {
  overflow: hidden;
}
/* mixin call for creating container content padding classes (arguments: rootContainer, contentSelector) */
.sapMITB.sapUiContentPadding > .sapMITBContainerContent > .sapMITBContent {
  padding: 1rem;
  box-sizing: border-box;
}
@media (max-width: 599px) {
  .sapMSplitContainerHideMode > .sapMSplitContainerDetail .sapMITB.sapUiResponsiveContentPadding > .sapMITBContainerContent > .sapMITBContent,
  .sapMSplitContainerPortrait > .sapMSplitContainerDetail .sapMITB.sapUiResponsiveContentPadding > .sapMITBContainerContent > .sapMITBContent,
  .sapMITB.sapUiResponsiveContentPadding > .sapMITBContainerContent > .sapMITBContent {
    padding: 0;
  }
}
@media (min-width: 600px) and (max-width: 1023px) {
  .sapMSplitContainerHideMode > .sapMSplitContainerDetail .sapMITB.sapUiResponsiveContentPadding > .sapMITBContainerContent > .sapMITBContent,
  .sapMITB.sapUiResponsiveContentPadding > .sapMITBContainerContent > .sapMITBContent {
    padding: 1rem;
    box-sizing: border-box;
  }
}
@media (min-width: 1024px) {
  .sapMSplitContainerHideMode > .sapMSplitContainerDetail .sapMITB.sapUiResponsiveContentPadding > .sapMITBContainerContent > .sapMITBContent,
  .sapMITB.sapUiResponsiveContentPadding > .sapMITBContainerContent > .sapMITBContent {
    padding: 1rem 2rem;
    box-sizing: border-box;
  }
}
.sapMSplitContainerMaster .sapMITB.sapUiResponsiveContentPadding > .sapMITBContainerContent > .sapMITBContent {
  padding: 0;
}
.sapMSplitContainerDetail .sapMITB.sapUiResponsiveContentPadding > .sapMITBContainerContent > .sapMITBContent {
  padding: 1rem;
}
.sapMITB.sapUiNoContentPadding > .sapMITBContainerContent > .sapMITBContent {
  padding: 0;
}
.sapMITH .sapMITBArrowScroll {
  cursor: pointer;
  font-size: 1rem;
  position: absolute;
  top: 1rem;
  color: #346187;
}
.sapMITBNotScrollable > .sapMITBArrowScroll {
  visibility: hidden;
}
.sapMITBScrollable.sapMITBNoScrollBack > .sapMITBArrowScrollLeft,
.sapMITBScrollable.sapMITBNoScrollBack > .sapMITBArrowScrollLeftTextOnly,
.sapMITBScrollable.sapMITBNoScrollForward > .sapMITBArrowScrollRight,
.sapMITBScrollable.sapMITBNoScrollForward > .sapMITBArrowScrollRightTextOnly {
  visibility: hidden;
}
.sapMITBArrowScroll:focus,
.sapMITBHead:focus,
.sapMITBFilterIcon:focus,
.sapMITBItem:focus {
  outline: none;
}
.sapMITBItem .sapMITBFilterIcon {
  overflow: visible;
}
.sapMITH {
  position: relative;
}
.sapMITH .sapMITHBtn.sapMBtnNoText {
  top: 1rem;
}
.sapMITH .sapMITHBtn {
  position: absolute;
  top: 1rem;
  right: 0;
  visibility: hidden;
  padding-right: 0.25rem;
  outline: none;
}
.sapMITH .sapMITHBtn.sapMBtnDragOver .sapMBtnInner {
  background: rgba(0, 112, 177, 0.05);
  border: 0.125rem solid #0070b1;
}
.sapMITBScrollable.sapMITH .sapMITHBtn {
  visibility: visible;
}
/* ------------------------------------------------ */
/* IconTabBar Scroll Container                      */
/* ------------------------------------------------ */
.sapMITBScrollContainer {
  display: block;
  margin: 0 1.75rem;
  /* the same padding as on the sapMITBHead is needed
	to correct the scrolling calculations*/
  padding: 0 1px;
  /* scrollbar from iscroll should not be visible */
}
.sapMITBScrollContainer::-webkit-scrollbar {
  display: none;
}
.sapMITBScrollContainer > div:nth-child(2) {
  visibility: hidden;
}
.sapMITHOverflowList .sapMITBScrollContainer {
  margin: 0 4.25rem 0 1.75rem;
}
.sapMITHOverflowList .sapMITBArrowScrollRight {
  right: 2.75rem;
}
.sapMITHOverflowList .sapMITBArrowScrollRightTextOnly {
  right: 2.75rem;
}
.sapMITBArrowScrollLeftTextOnly {
  left: 0.25rem;
  padding: 1.0625rem 0.4rem;
}
.sapMITBArrowScrollRightTextOnly {
  padding: 1.0625rem 0.4rem;
  right: 0.25rem;
}
