  text-align: left;
}
.sapUiCalHead > button,
.sapUiCalCancel {
  background-color: transparent;
  border: none;
  color: #0a6ed1;
  cursor: pointer;
  overflow: hidden;
  white-space: nowrap;
  height: 2.5rem;
  padding: 0;
  font-size: 0.875rem;
}
.sapUiCalHeadB {
  vertical-align: top;
  width: 5.75rem;
