  color: #6a6d70;
  font-size: 0.625rem;
}
.sapUiCalHeadPrev,
.sapUiCalHeadNext {
  position: absolute;
  width: 2.5rem;
}
.sapUiCalHeadPrev {
  left: 0.5rem;
}
.sapUiCalHeadNext {
  right: 0.5rem;
}
/* no default (browser) outline, if device needs focus visualization its defined later */
.sapUiCalHead > button:focus,
.sapUiCalCancel:focus {
  outline: none;
}
/* hover and focus makes only sense on desktops. On mobile devices it's not needed */
[data-sap-ui-wc-root].sap-desktop .sapUiCalHead > button:focus,
[data-sap-ui-wc-root].sap-desktop .sapUiCalCancel:focus {
