/* =========================== */
/* Global CSS for 'base' theme */
/* =========================== */
:host(ui5-card) {
  display: inline-block;
}
ui5-card,
:host(ui5-card) {
  display: inline-block;
  width: 100%;
}
.sapFCard {
  background: #fff;
  box-shadow: 0 0 0 1px rgba(0, 0, 0, 0.15);
  border-radius: 0.25rem;
  border: 1px solid #cccccc;
  width: 100%;
  height: 100%;
  overflow: hidden;
  font-family: "72", Arial, Helvetica, sans-serif;
  font-size: 16px;
  color: #32363a;
}
.sapFCard .sapFCardHeader {
  background: #fff;
  border-bottom: 1px solid #cccccc;
  padding: 1rem;
  display: flex;
  align-items: flex-start;
}
.sapFCard .sapFCardHeader:hover {
  background: #fafafa;
}
.sapFCard .sapFCardHeader:active {
  background: #f0f0f0;
}
.sapFCard .sapFCardHeader .sapFCardAvatar {
  height: 3rem;
  width: 3rem;
  margin-right: 0.75rem;
  display: flex;
  align-items: center;
  justify-content: center;
}
.sapFCard .sapFCardHeader .sapFCardAvatar .sapFCardHeaderIcon {
  font-size: 1.5rem;
  color: #6a6d70;
}
.sapFCard .sapFCardHeader .sapFCardHeaderText {
  flex: 1;
}
.sapFCard .sapFCardHeader .sapFCardHeaderText .sapFCardTitle {
  font-family: "72", Arial, Helvetica, sans-serif;
  font-size: 1rem;
  font-weight: normal;
  color: #32363a;
  white-space: normal;
  word-wrap: break-word;
  overflow: hidden;
  text-align: left;
  text-overflow: ellipsis;
  -webkit-line-clamp: 3;
  -webkit-box-orient: vertical;
  display: -webkit-box;
  max-width: 100%;
  max-height: 3.5rem;
}
.sapFCard .sapFCardHeader .sapFCardHeaderText .sapFCardSubtitle {
  font-family: "72", Arial, Helvetica, sans-serif;
  font-size: 0.875rem;
  font-weight: normal;
  color: #6a6d70;
  white-space: normal;
  word-wrap: break-word;
  overflow: hidden;
  text-align: left;
  text-overflow: ellipsis;
  margin-top: 0.5rem;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
  display: -webkit-box;
  max-width: 100%;
  max-height: 2.1rem;
}
.sapFCard .sapFCardHeader .sapFCardStatus {
  font-family: "72", Arial, Helvetica, sans-serif;
  font-size: 0.75rem;
  color: #6a6d70;
  text-align: left;
  line-height: 1.125rem;
  padding-left: 1rem;
  margin-left: auto;
  text-wrap: avoid;
  display: inline-block;
}
.sapFCard .sapFCardContent .sapFCardListItem {
  width: 100%;
}
.sapFCard .sapFCardContent .sapFCardListItemTitle {
  overflow: hidden;
  text-overflow: ellipsis;
  color: #32363a;
  font-size: 1rem;
}
.sapFCard .sapFCardContent .sapFCardListItemDescription {
  overflow: hidden;
  text-overflow: ellipsis;
  font-size: 0.875rem;
  color: #6a6d70;
}
