/* ============================= */
/* Base theme                    */
/* ============================= */
/* ============================= */
/* Global Base parameters        */
/* ============================= */
/* =========================== */
/* Global CSS for 'base' theme */
/* =========================== */
/* =========================== */
/* CSS for control sap.m/Link  */
/* Base theme                  */
/* =========================== */
.sapMLnk {
  color: #0a6ed1;
  text-decoration: none;
  display: inline-block;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  word-wrap: normal;
  font-family: "72", Arial, Helvetica, sans-serif;
  font-size: 0.875rem;
  cursor: pointer;
}
.sapMLnk:active {
  background-color: transparent;
  /* required for IE10 */
}
.sapMLnk:active:not(.sapMLnkDsbl),
.sapMLnk:focus:not(.sapMLnkDsbl) {
  color: #0a6ed1;
}
.sapMLnk:hover:not(.sapMLnkDsbl) {
  color: #0854a0;
}
.sapUiInverted-CTX .sapUiLnk {
  color: #acd4fb;
}
.sapMLnk.sapMLnkWrapping {
  white-space: normal;
  word-wrap: break-word;
}
.sapMLnkMaxWidth {
  max-width: 100%;
}
/* Link in sap.ui.table.* tables */
.sapUiTableCell .sapMLnk {
  vertical-align: middle;
}
.sapUiSizeCondensed .sapUiTableCell .sapMLnk {
  line-height: 1.5rem;
}
.sapUiHLayoutChildWrapper > .sapMLnk {
  vertical-align: text-bottom;
}
.sapMFT .sapMLnk {
  vertical-align: text-top;
}
/* Global focus offset */
[data-sap-ui-wc-root].sap-desktop .sapMLnk:focus:not(.sapMLnkDsbl) {
  outline-offset: -1px;
  text-decoration: underline;
}
/* Link focus offset and visualization in sap.ui.table.* tables */
[data-sap-ui-wc-root].sap-desktop .sapUiSizeCondensed .sapUiTableCell .sapMLnk:focus:not(.sapMLnkDsbl) {
  outline-offset: -2px;
}
.sapUiAnalyticalTable .sapUiAnalyticalTableSum .sapUiTableCell .sapMLnk {
  font-weight: bold;
}
/* IE and Edge ignores outline-offset. Use an overlay: */
/* TODO remove after 1.62 version */
[data-sap-ui-wc-root][data-sap-ui-browser^="ie"].sap-desktop .sapMLnk:focus:not(.sapMLnkDsbl),
[data-sap-ui-wc-root][data-sap-ui-browser^="ed"].sap-desktop .sapMLnk:focus:not(.sapMLnkDsbl) {
  outline: none;
  position: relative;
}
[data-sap-ui-wc-root][data-sap-ui-browser^="ie"].sap-desktop .sapMLnk:focus:not(.sapMLnkDsbl):before,
[data-sap-ui-wc-root][data-sap-ui-browser^="ed"].sap-desktop .sapMLnk:focus:not(.sapMLnkDsbl):before {
  content: " ";
  box-sizing: border-box;
  width: 100%;
  height: 100%;
  position: absolute;
  left: 0;
  top: 0;
  border: 1px dashed #000;
  pointer-events: none;
}
/* Hover Decoration only for desktop clients*/
[data-sap-ui-wc-root].sap-desktop .sapMLnk:hover:not(.sapMLnkDsbl) {
  text-decoration: underline;
}
[data-sap-ui-wc-root].sap-desktop .sapMLnk:focus:not(.sapMLnkDsbl) {
  outline: 1px dotted #000;
}
.sapUiFormEdit .sapMLnk {
  line-height: normal;
  margin-top: 1rem;
  margin-bottom: 0.75rem;
  /* for some reason browser adds 4px, display: block would lead to stretched focus outline*/
}
.sapUiFormToolbar .sapMTB .sapMLnk {
  line-height: normal;
  margin-top: auto;
  margin-bottom: auto;
}
.sapMLnkEmphasized {
  font-weight: bold;
}
/* Compact size */
.sapUiSizeCompact .sapUiFormEdit .sapMLnk {
  margin-top: 0.5rem;
  margin-bottom: 0.25rem;
}
.sapMLnk:-webkit-any-link:focus {
  outline: none;
}
