/* =============================== */
/* CSS for ui5-list                */
/* Base theme                      */
/* =============================== */
ui5-list,
:host(ui5-list) {
  display: block;
  max-width: 100%;
}
.sapMList {
  width: 100%;
  height: 100%;
  position: relative;
  box-sizing: border-box;
}
.sapMList.sapMListInsetBG {
  padding: 2rem;
}
.sapMList .sapMListUl {
  list-style-type: none;
  padding: 0;
  margin: 0;
}
.sapMList .sapMListUl:focus {
  outline: none;
}
.sapMList .sapMListDummyArea {
  position: fixed;
  /* keep it in the visible viewport, so that IE does not scroll on focus */
}
.sapMList .sapMListNoData {
  list-style-type: none;
  display: -webkit-box;
  display: flex;
  -webkit-box-align: center;
  align-items: center;
  -webkit-box-pack: center;
  justify-content: center;
  color: #32363a;
  background-color: #fff;
  border-bottom: 1px solid #ededed;
  padding: 0 1rem !important;
  height: 3rem;
}
.sapMList .sapMListHdrText {
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
  box-sizing: border-box;
  font-size: 1.125rem;
  font-family: "72", Arial, Helvetica, sans-serif;
  color: #32363a;
  height: 3rem;
  line-height: 3rem;
  padding: 0 1rem;
  background-color: transparent;
  border-bottom: 1px solid #d9d9d9;
}
.sapMList .sapMListFtr {
  height: 2rem;
  box-sizing: border-box;
  -webkit-text-size-adjust: none;
  /* To improve readability Mobile Safari automatically increases the size of small text so let's disable this */
  font-size: 0.875rem;
  line-height: 2rem;
  background-color: #fafafa;
  color: #32363a;
  padding: 0 1rem;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}
.sapMList .sapMListShowSeparatorsNone .sapMListNoData {
  border-bottom: 0px;
}
.sapMList .sapMListNoDataText {
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}
/* Compact size */
.sapUiSizeCompact .sapMList .sapMListNoData {
  height: 2rem;
  font-size: 0.875rem;
}
