Input.css:
/* ============================= */
/* Base theme                    */
/* ============================= */
/* ============================= */
/* Global Base parameters        */
/* ============================= */
/* =========================== */
/* Global CSS for 'base' theme */
/* =========================== */
/* ================================ */
/* CSS for control sap.m/InputBase  */
/* Base theme                       */
/* ================================ */
:host(ui5-input) {
  display: inline-block;
  width: 100%;
}
.sapMInputBase {
  height: 2.5rem;
  background: transparent;
  position: relative;
  display: inline-block;
  vertical-align: top;
  outline: none;
  box-sizing: border-box;
  line-height: 0;
}
.sapMInputBaseNoWidth {
  max-width: 100%;
}
.sapMInputBaseHeightMargin {
  margin: 0.25rem 0;
}
.sapMInputBaseInner {
  background: transparent;
  border: none;
  font-style: normal;
  -webkit-appearance: none;
  -moz-appearance: textfield;
  font-size: 0.875rem;
  font-family: "72", Arial, Helvetica, sans-serif;
  line-height: normal;
  padding: 0 0.75rem;
  height: 100%;
  box-sizing: border-box;
  flex: auto;
  min-width: 3rem;
  text-overflow: ellipsis;
}
.sapMInputBaseContentWrapper {
  height: 100%;
  box-sizing: border-box;
  display: inline-flex;
  flex-direction: row;
  justify-content: flex-end;
  position: relative;
  overflow: hidden;
  min-width: -moz-min-content;
  min-width: min-content;
}
.sapMInputBaseHasBeginIcons .sapMInputBaseInner {
  padding-left: 0.25rem;
}
.sapMInputBaseHasEndIcons .sapMInputBaseInner {
  padding-right: 0.25rem;
}
.sapUiIcon.sapMInputBaseIcon {
  width: 2.375rem;
  min-width: 2.375rem;
  height: 100%;
  line-height: 2.375rem;
  vertical-align: middle;
  cursor: pointer;
  background: transparent;
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;
}
.sapMInputDescriptionWrapper {
  display: inline-block;
  overflow: hidden;
  text-overflow: ellipsis;
  vertical-align: top;
}
.sapMFocus .sapMInputBaseContentWrapper::before {
  content: "";
  position: absolute;
  top: 1px;
  left: 1px;
  right: 1px;
  bottom: 1px;
  pointer-events: none;
  z-index: 1;
}
[data-sap-ui-wc-root][data-sap-ui-browser^="ie"].sap-desktop .sapMFocus .sapMInputBaseContentWrapper:not(.sapMInputBaseDisabledWrapper)::before,
[data-sap-ui-wc-root][data-sap-ui-browser^="ed"].sap-desktop .sapMFocus .sapMInputBaseContentWrapper:not(.sapMInputBaseDisabledWrapper)::before {
  content: " ";
  position: absolute;
  top: 1px;
  left: 1px;
  right: 1px;
  bottom: 1px;
  border: 2px dashed #000;
  pointer-events: none;
  z-index: 1;
}
.sapMInputBaseInner::-webkit-input-placeholder {
  font-size: 0.875rem;
  font-style: italic;
}
.sapMInputBaseInner::-moz-placeholder {
  font-size: 0.875rem;
  font-style: italic;
}
.sapMInputBaseInner:-ms-input-placeholder {
  font-size: 0.875rem;
  font-style: italic;
}
.sapMInputBaseInner {
  width: 100%;
}
.sapMInputBaseInner::-webkit-outer-spin-button,
.sapMInputBaseInner::-webkit-inner-spin-button {
  -webkit-appearance: none;
  margin: 0;
}
.sapMInputBaseInner:focus {
  outline: none;
  outline-width: 0;
}
/* remove user agent pseudo-element clear button (IE specifix) */
.sapMInputBaseInner::-ms-clear {
  /* note: don't use display: none; see BCP 1570024996 */
  height: 0;
}
.sapMPointer > .sapMInputBaseInner {
  cursor: pointer;
}
.sapMInputBaseDisabled {
  opacity: 0.5;
}
.sapMInputBaseDisabled .sapMInputBaseIcon {
  cursor: default;
}
.sapMInputBaseInner[placeholder] {
  text-overflow: ellipsis;
}
.sapMInputBaseInner::-webkit-input-placeholder {
  color: #74777a;
  text-overflow: ellipsis !important;
  /* important is necessary to overwrite inline style of shadow dom */
}
.sapMInputBaseInner::-moz-placeholder {
  color: #74777a;
}
.sapMInputBaseInner:-ms-input-placeholder {
  color: #74777a;
}
input.sapMInputBaseInner:invalid {
  /* disable native browser input validation visualization e.g. for type email or number */
  box-shadow: none;
}
/*read only*/
.sapMInputBaseReadonly .sapMInputBaseContentWrapper {
  border-color: #979ea6;
  background: rgba(242, 242, 242, 0.5);
}
.sapMInputBaseInner:not(.sapMInputBaseReadonlyWrapper):not(.sapMInputBaseContentWrapperState):focus {
  border-color: #89919a;
}
[data-sap-ui-wc-root][data-sap-ui-browser^="ie"].sap-desktop .sapMFocus .sapMInputBaseContentWrapper:not(.sapMInputBaseDisabledWrapper)::before,
[data-sap-ui-wc-root][data-sap-ui-browser^="ed"].sap-desktop .sapMFocus .sapMInputBaseContentWrapper:not(.sapMInputBaseDisabledWrapper)::before {
  border: 1px dashed #000;
}
.sapMInputBaseContentWrapper:not(.sapMInputBaseReadonlyWrapper):not(.sapMInputBaseContentWrapperState):not(.sapMInputBaseDisabledWrapper):hover {
  background: #fff;
  border-color: #0854a0;
}
.sapMInputBaseContentWrapper:not(.sapMInputBaseReadonlyWrapper):not(.sapMInputBaseContentWrapperState):not(.sapMInputBaseDisabledWrapper):active {
  background: #fff;
  border-color: #0854a0;
}
.sapMILI-CTX .sapMInputBaseContentWrapper,
.sapMILI-CTX .sapMInputBaseInner,
.sapMILI-CTX .sapMInputBaseDisabled .sapMInputBaseInner,
.sapMILI-CTX .sapMInputBaseReadonlyWrapper .sapMInputBaseInner {
  text-align: right;
  background: transparent;
}
.sapMLIBActive .sapMInputBaseInner {
  color: #fff;
  background: transparent;
}
.sapMLIBActive .sapMInputBaseInner::-webkit-input-placeholder {
  color: #fff;
}
/* Compact size */
.sapUiSizeCompact .sapMInputBase {
  line-height: 1rem;
  height: 1.625rem;
}
.sapUiSizeCompact .sapMInputBaseContentWrapper {
  height: 1.625rem;
}
.sapUiSizeCompact .sapMInputBaseInner {
  padding: 0 0.5rem;
  min-width: 2rem;
}
.sapUiSizeCompact .sapMInputBaseIcon {
  width: 2rem;
  min-width: 2rem;
  line-height: 1.5rem;
}
.sapUiSizeCompact .sapMInputBaseContentWrapper.sapMInputBaseContentWrapperState .sapMInputBaseIcon {
  line-height: 1.375rem;
}
.sapUiSizeCompact .sapMInputDescriptionWrapper span {
  line-height: 1.625rem;
}
.sapUiSizeCompact .sapMInputBaseHasEndIcons .sapMInputBaseInner {
  padding-right: 0.25rem;
}
.sapUiSizeCompact .sapMInputBaseHeightMargin {
  margin: 0.1875rem 0;
}
/* Condensed size */
.sapUiSizeCondensed .sapUiTableCell .sapMInputBase {
  height: 1.375rem;
  /* + 2*1px padding = 1.5rem */
  padding: 0 0.0625rem;
  /* 1px padding only */
}
.sapUiSizeCondensed .sapUiTableCell .sapMInputBaseHeightMargin {
  margin: 0;
}
.sapUiSizeCondensed .sapUiTableCell .sapMInputBaseInner:not(.sapMInputBaseContentWrapperState) {
  border-color: transparent;
}
.sapUiSizeCondensed .sapUiTableCell .sapMInputBaseContentWrapper {
  height: 1.375rem;
}
.sapUiSizeCondensed .sapUiTableCell .sapMInputDescriptionWrapper span {
  line-height: 1.375rem;
}
/* Input selection fix -
 * Due to the fact that the CSS parsing rules require dropping the whole rule when encountering an invalid
 * pseudo-element, two separate rules must be written */
.sapMInputBaseInner::selection,
.sapMInputBaseInner::-moz-selection {
  background: #0a6ed1;
  color: #fff;
}
/* Google chrome and Safari input selection fix:
 * The browser renders the selection background with 50% opacity by default.
 * The solution is to use fade with a value for alpha 99 if it's set 100 the browser ignores it.
 */
[data-sap-ui-wc-root][data-sap-ui-browser^="sf"] .sapMInputBaseInner::selection,
[data-sap-ui-wc-root][data-sap-ui-browser^="cr"] .sapMInputBaseInner::selection {
  background: rgba(10, 110, 209, 0.99);
  color: rgba(255, 255, 255, 0.99);
}
[data-sap-ui-wc-root][data-sap-ui-browser^="ed"] .sapMInputBaseInner {
  line-height: 0;
}
.sapMInputBaseDisabled .sapMInputBaseInner::-webkit-input-placeholder {
  opacity: 0;
}
.sapMInputBaseDisabled .sapMInputBaseInner::-moz-placeholder {
  opacity: 0;
}
.sapMInputBaseDisabled .sapMInputBaseInner:-ms-input-placeholder {
  color: #fff;
}
[data-sap-ui-wc-root][data-sap-ui-browser^="ie"] .sapMFocus .sapMInputBaseDisabledWrapper::before {
  display: none;
  pointer-events: none;
}
.sapMInputBaseReadonly .sapMInputBaseInner::-webkit-input-placeholder {
  opacity: 0;
}
.sapMInputBaseReadonly .sapMInputBaseInner::-moz-placeholder {
  opacity: 0;
}
.sapMInputBaseReadonly .sapMInputBaseInner:-ms-input-placeholder {
  color: rgba(242, 242, 242, 0.5);
}
.sapMInputBaseDisabledWrapper .sapMInputBaseIcon {
  color: rgba(50, 54, 58, 0.5);
}
/* pressed */
.sapMInputBaseIconPressed .sapMInputBaseIcon,
[data-sap-ui-wc-root].sap-desktop .sapMInputBaseIconPressed .sapMInputBaseIcon:hover {
  background: #0854a0;
}
.sapMInputBaseIconPressed .sapMInputBaseIcon::before {
  color: #fff;
}
.sapMInputBaseIconPressed .sapMInputBaseContentWrapper:not(.sapMInputBaseReadonlyWrapper):not(.sapMInputBaseContentWrapperState):not(.sapMInputBaseDisabledWrapper) {
  border-color: #0854a0;
}
