}
.sapMInputBaseIcon {
  color: #0a6ed1;
  border-left: 1px solid #89919a;
  background: rgba(224, 224, 224, 0.5);
}
.sapMInputBaseIcon:hover {
  border-color: #0854a0;
}
.sapMInputBaseIcon:active {
  background: #eef5fc;
  border-color: #0854a0;
}
.sapMInputBaseDisabledWrapper .sapMInputBaseIcon {
  color: rgba(50, 54, 58, 0.5);
  background-color: #fff;
}
.sapMInputBaseContentWrapperWarning {
  background: #fff;
  border-color: #e9730c;
}
.sapMInputBaseContentWrapperSuccess {
  background: #fff;
  border-color: #107e3e;
}
.sapMInputBaseContentWrapperError {
  background: #fff;
  border-color: #bb0000;
}
.sapMInputBaseReadonlyWrapper,
.sapMInputBaseDisabledWrapper {
  background: rgba(242, 242, 242, 0.5);
}
.sapMFocus .sapMInputBaseContentWrapper::before {
  border: 1px dotted #000;
}
[data-sap-ui-wc-root][data-sap-ui-browser^="ie"].sap-desktop .sapMFocus .sapMInputBaseContentWrapper:not(.sapMInputBaseDisabledWrapper)::before,
[data-sap-ui-wc-root][data-sap-ui-browser^="ed"].sap-desktop .sapMFocus .sapMInputBaseContentWrapper:not(.sapMInputBaseDisabledWrapper)::before {
  border: 1px dashed #000;
}
