Popup.css:
/* ============================= */
/* Base theme                    */
/* ============================= */
/* ============================= */
/* Global Base parameters        */
/* ============================= */
/* =========================== */
/* Global CSS for 'base' theme */
/* =========================== */
.sapMPopupFrame {
  width: 0;
  height: 0;
  display: none;
  visibility: visible;
}
.sapMPopupFrameOpen {
  display: inline;
}
.sapMPopup {
  color: #fff;
  min-width: 6.25rem;
  box-sizing: border-box;
  outline: none;
  max-width: 100%;
  max-height: 100%;
  background: #fff;
  border: none;
  box-shadow: 0 0.625rem 1.875rem 0 rgba(0, 0, 0, 0.15), 0 0 0 1px rgba(0, 0, 0, 0.15);
  border-radius: 0.25rem;
  min-height: 2rem;
}
.sapMPopup .sapMPopupHeader {
  margin: 0;
  color: #505559;
  font-size: 1rem;
  font-weight: 400;
  font-family: "72", Arial, Helvetica, sans-serif;
  border-bottom: 1px solid #ebebeb;
}
.sapMPopup .sapMPopupHeaderText {
  padding: 0 0.25rem;
  text-align: center;
  height: 3rem;
  line-height: 3rem;
}
.sapMPopup .sapMPopupFooter {
  font-size: 1rem;
  font-weight: 400;
  font-family: "72", Arial, Helvetica, sans-serif;
  background: #fff;
  border-top: 1px solid #ebebeb;
  color: #505559;
}
.sapMPopup .sapMPopupContent {
  overflow: auto;
  position: relative;
  box-sizing: border-box;
  background-color: #fff;
}
.sapMPopup .sapMPopupScroll {
  vertical-align: middle;
  box-sizing: border-box;
  padding: 0.4375em;
}
.sapUiBLy {
  background-color: #000000;
  opacity: 0.6;
  filter: alpha(opacity=60);
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  position: fixed;
  outline: 0 none;
}
.sapMPopupBlockLayer {
  visibility: visible;
}
.sapMPopupBlockLayerHidden {
  display: none;
}
