ListItemBase.css:
/* ============================= */
/* Base theme                    */
/* ============================= */
/* ============================= */
/* Global Base parameters        */
/* ============================= */
/* =========================== */
/* Global CSS for 'base' theme */
/* =========================== */
/* =================================== */
/* CSS for control sap.m/ListItemBase  */
/* Base theme                          */
/* =================================== */
:host(ui5-li),
:host(ui5-li-custom) {
  display: block;
}
.sapMLIB {
  display: -webkit-box;
  display: flex;
  height: 3rem;
  -webkit-box-align: center;
  align-items: center;
  position: relative;
  background: #fff;
  padding: 0 1rem 0 1rem;
  box-sizing: border-box;
}
.sapMLIBHoverable:hover {
  background: #fafafa;
}
.sapMLIB.sapMLIBSelected {
  background: #e5f0fa;
}
.sapMLIB.sapMLIBActive {
  color: #fff;
  background: #0854a0;
}
.sapMLIB.sapMLIBHoverable.sapMLIBSelected:hover {
  background: #d8e8f7;
}
.sapMLIB.sapMLIBHoverable.sapMLIBSelected.sapMLIBActive:hover {
  background: #0854a0;
}
.sapMLIB.sapMLIBFocusable:focus {
  outline: none;
}
.sapMLIB.sapMLIBFocusable:focus:after,
.sapMLIB.sapMLIBFocusable .sapMLIBContent:focus:after {
  content: "";
  border: 1px dotted #000;
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  pointer-events: none;
}
.sapMLIB.sapMLIBActive.sapMLIBFocusable:focus,
.sapMLIB.sapMLIBActive.sapMLIBFocusable .sapMLIBContent:focus {
  outline-color: #fff;
}
.sapMLIB.sapMLIBTransparent {
  background: rgba(255, 255, 255, 0);
}
.sapMLIB.sapMLIBBorder {
  border-bottom: 1px solid #ededed;
}
.sapMLIB.sapMLIBActive .sapMLIBImgNav {
  color: #fff;
}
.sapMLIBImgNav {
  -webkit-box-flex: 0;
  flex: none;
  width: 3rem;
  font-size: 0.75rem;
  color: #6a6d70;
  text-align: center;
  box-sizing: border-box;
}
.sapMLIBContent {
  max-width: 100%;
  min-height: 100%;
  font-family: "72", Arial, Helvetica, sans-serif;
}
/* IE ignores outline-offset. Use an overlay: */
.sapMLIBLegacyOutline:focus {
  outline: none;
  position: relative;
}
.sapMLIBLegacyOutline:focus::before {
  content: "";
  border: 0;
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  pointer-events: none;
  border: 1px dotted #000;
}
.sapMLIBActionable,
.sapMLIBActionable > .sapMLIBImgNav {
  cursor: pointer;
}
.sapMLIBFocusable.sapMLIBLegacyOutline:focus {
  outline: none;
}
:not(.sapMListModeDelete) > .sapMLIB.sapMLIBTypeNavigation {
  padding-right: 0;
}
