Calendar.css:
/* ============================= */
/* Base theme                    */
/* ============================= */
/* ============================= */
/* Global Base parameters        */
/* ============================= */
/* =========================== */
/* Global CSS for 'base' theme */
/* =========================== */
/* ======================================== */
/* CSS for control sap.ui.unified/Calendar  */
/* Base theme                               */
/* ======================================== */
.sapUiCal {
  background: none repeat scroll 0 0 #fff;
  overflow: hidden;
  position: relative;
  outline: none;
  display: inline-block;
  white-space: initial;
  /* To not inherit */
  box-sizing: border-box;
  text-align: center;
  padding: 1rem 0.5rem 0 0.5rem;
}
.sapUiCalInt {
  box-sizing: border-box;
}
/* calendar should have only a border if on popup */
.sapUiCal.sapUiShd {
  border: 0.0625rem solid transparent;
  width: 20.125rem;
}
/*** HEADER ***/
.sapUiCalHead {
  width: 100%;
  position: relative;
  text-align: center;
  height: 2.5rem;
  padding-top: 0.25rem;
  padding-bottom: 0.25rem;
  background-color: #fff;
}
.sapUiCal:not(.sapUiCalInt) > .sapUiCalHead {
  max-width: 40rem;
}
.sapUiCalTwoMonthsTwoColumns:not(.sapUiCalInt) > .sapUiCalHead {
  max-width: 100%;
  box-sizing: border-box;
  text-align: left;
}
.sapUiCalHead > button,
.sapUiCalCancel {
  background-color: transparent;
  border: none;
  color: #0a6ed1;
  cursor: pointer;
  overflow: hidden;
  white-space: nowrap;
  height: 2.5rem;
  padding: 0;
  font-size: 0.875rem;
}
.sapUiCalHeadB {
  vertical-align: top;
  width: 5.75rem;
}
.sapUiCalHeadBAddText {
  width: 100%;
  float: left;
  color: #6a6d70;
  font-size: 0.625rem;
}
.sapUiCalHeadPrev,
.sapUiCalHeadNext {
  position: absolute;
  width: 2.5rem;
}
.sapUiCalHeadPrev {
  left: 0.5rem;
}
.sapUiCalHeadNext {
  right: 0.5rem;
}
/* no default (browser) outline, if device needs focus visualization its defined later */
.sapUiCalHead > button:focus,
.sapUiCalCancel:focus {
  outline: none;
}
/* hover and focus makes only sense on desktops. On mobile devices it's not needed */
[data-sap-ui-wc-root].sap-desktop .sapUiCalHead > button:focus,
[data-sap-ui-wc-root].sap-desktop .sapUiCalCancel:focus {
  background-color: #fafafa;
  color: #0a6ed1;
  outline: 1px dotted #000;
  outline-offset: -3px;
}
[data-sap-ui-wc-root].sap-desktop .sapUiCalHead > button:hover,
[data-sap-ui-wc-root].sap-desktop .sapUiCalCancel:hover {
  background-color: #fafafa;
  color: #0a6ed1;
}
/* once for desktop to overwrite focus and once for all devices needed */
.sapUiCalHead > button:active,
.sapUiCalCancel:active,
[data-sap-ui-wc-root].sap-desktop .sapUiCalHead > button:active,
[data-sap-ui-wc-root].sap-desktop .sapUiCalHead > button:active > .sapUiCalHeadBAddText,
[data-sap-ui-wc-root].sap-desktop .sapUiCalCancel:active {
  background-color: #0854a0;
  color: #fff;
}
.sapUiCalHead > button.sapUiCalDsbl {
  cursor: default;
  opacity: .3;
  filter: alpha(opacity=30);
}
[data-sap-ui-wc-root].sap-desktop .sapUiCalHead > button.sapUiCalDsbl:focus,
.sapUiCalHead > button.sapUiCalDsbl:hover,
.sapUiCalHead > button.sapUiCalDsbl:active {
  background-color: transparent;
  color: #0a6ed1;
}
/*** days, months, years ***/
.sapUiCalMonthView {
  vertical-align: top;
}
.sapUiCalMonthView,
.sapUiCalMonthPicker,
.sapUiCalYearPicker {
  width: 14em;
  display: inline-block;
  width: 18rem;
  /* +1rem padding left and right = 320 px*/
}
.sapUiCalMonthView,
.sapUiCalMonthPicker,
.sapUiCalYearPicker,
.sapUiCalRow {
  background-color: #fff;
}
.sapUiCal .sapUiCalMonthView,
.sapUiCal .sapUiCalRow {
  box-sizing: border-box;
  padding-left: 0.5rem;
  padding-right: 0.5rem;
  padding-bottom: 1rem;
}
.sapUiCalInt > .sapUiCalContent > .sapUiCalMonthPicker,
.sapUiCalInt > .sapUiCalContent > .sapUiCalYearPicker {
  width: auto;
}
.sapUiCalHeadText {
  text-align: center;
  height: 2rem;
  line-height: 2rem;
  color: #c0399f;
}
.sapUiCalContentSingle .sapUiCalHeadText {
  text-align: left;
  margin-left: 12.5%;
}
.sapUiCalRow .sapUiCalHeadText {
  float: left;
  text-align: left;
  overflow: hidden;
  text-overflow: ellipsis;
  whitespace: nowrap;
}
.sapUiCalRow div > .sapUiCalHeadText:last-child {
  float: none;
  /* if not set, in IE the item line is displayed in the same line */
}
.sapUiCalItems {
  display: inline-block;
  width: 100%;
  white-space: nowrap;
}
.sapUiCalWH,
.sapUiCalItem {
  display: block;
  float: left;
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;
  cursor: default;
  text-align: center;
  overflow: hidden;
  white-space: nowrap;
  box-sizing: border-box;
}
.sapUiCalMonthView .sapUiCalWH,
.sapUiCalMonthView .sapUiCalItem {
  width: 12.5%;
}
.sapUiCalMonthView.sapUiCalNoWeekNum .sapUiCalWH,
.sapUiCalMonthView.sapUiCalNoWeekNum .sapUiCalItem {
  width: 14.2857%;
}
.sapUiCalItemText {
  width: 100%;
  height: 100%;
  display: inline-block;
  box-sizing: border-box;
}
.sapUiCalItem,
.sapUiCalWeekNum {
  height: 3rem;
  line-height: 2.875rem;
  font-size: 0.875rem;
}
.sapUiCalWeekNum {
  position: absolute;
  right: 100%;
  width: 100%;
  color: #32363a;
  font-size: 11px;
  cursor: default;
  top: -0.0625rem;
}
.sapUiCalWH {
  color: #32363a;
  white-space: nowrap;
  height: 2rem;
  line-height: 1.875rem;
  font-size: 0.75rem;
}
.sapUiCalItem {
  color: #32363a;
  background-color: #7b7f82;
}
.sapUiCalItemDsbl {
  color: rgba(50, 54, 58, 0.5);
  background-color: rgba(118, 122, 125, 0.5);
}
[data-sap-ui-wc-root].sap-desktop .sapUiCalItem:focus {
  background-color: #5e6063;
}
.sapUiCalMonthView .sapUiCalFirstWDay {
  clear: both;
  margin-left: 12.5%;
  position: relative;
  overflow: visible;
}
.sapUiCalMonthView.sapUiCalNoWeekNum .sapUiCalFirstWDay {
  margin-left: 0;
}
.sapUiCalItemOtherMonth {
  color: rgba(50, 54, 58, 0.5);
}
.sapUiCalMulti .sapUiCalItemOtherMonth {
  visibility: hidden;
}
.sapUiCalItemNow {
  color: #0a6ed1;
}
.sapUiCalItemWeekEnd {
  background-color: #636568;
}
.sapUiCalItemWeekEnd.sapUiCalItemDsbl {
  background-color: rgba(106, 109, 112, 0.5);
}
.sapUiCalItemSel {
  background-color: #0a6ed1;
}
.sapUiCalContent {
  position: relative;
  text-align: center;
}
.sapUiCalContentOver {
  background-color: #000;
  opacity: 0.15;
  filter: alpha(opacity=60);
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  position: absolute;
  outline: 0 none;
}
.sapUiCalContent > .sapUiCalMonthPicker,
.sapUiCalContent > .sapUiCalYearPicker,
.sapUiCalTimeInt > .sapUiCalContent > .sapUiCalDatesRow {
  position: absolute;
  top: 18px;
  bottom: 0;
  left: 0;
  right: 0;
  background-color: #fff;
}
.sapUiCalTimeInt > .sapUiCalContent > .sapUiCalDatesRow .sapUiCalWH {
  display: none;
}
/* Day names inside day */
.sapUiCalNoNameLine > .sapUiCalItems > .sapUiCalItem {
  position: relative;
}
.sapUiCalNoNameLine > .sapUiCalItems > .sapUiCalItem > .sapUiCalDayName {
  position: absolute;
  top: 0;
  right: 0;
  height: 33%;
  width: 100%;
  color: #32363a;
  overflow: hidden;
  white-space: nowrap;
  line-height: 100%;
}
.sapUiCalItemTextAmPm {
  font-size: 11px;
  vertical-align: top;
}
.sapUiCalCancel {
  display: none;
  /* cancel button only visible on phones */
  position: absolute;
}
/* 2 calendar types */
.sapUiCalMonthSecType > .sapUiCalItems .sapUiCalItem {
  position: relative;
}
.sapUiCalMonthSecType > .sapUiCalItems .sapUiCalItem > .sapUiCalItemSecText {
  position: absolute;
  bottom: 0;
  right: 0;
  height: 33%;
  width: 50%;
  color: #6a6d70;
  overflow: hidden;
  white-space: nowrap;
  line-height: 100%;
}
/* Day, Month and YearPicker on popup */
.sapUiCalMonthView[data-sap-ui-popup],
.sapUiCalMonthPicker[data-sap-ui-popup],
.sapUiCalYearPicker[data-sap-ui-popup] {
  padding: 1rem;
}
/* tablet(large) size is default */
.sapUiCal:not(.sapUiCalInt) {
  width: 20rem;
  min-width: 20rem;
}
.sapUiCalMulti:not(.sapUiCalInt) {
  width: 39rem;
  /* to display 2 months side by side as default */
}
/* calendar should have only a border if on popup */
.sapUiCal[data-sap-ui-popup] {
  box-shadow: 0 0.625rem 1.875rem 0 rgba(0, 0, 0, 0.15), 0 0 0 1px rgba(0, 0, 0, 0.15);
}
[data-sap-ui-wc-root].sapUiMedia-Std-Phone .sapUiCalHeadB0 {
  width: 3rem;
  /* do not overflow buttons on phone */
}
.sapUiCalHeadBFirst {
  margin-right: 0.25rem;
}
.sapUiCalHeadBLast {
  margin-left: 0.25rem;
}
.sapUiCalMulti .sapUiCalHeadB1,
.sapUiCalMonthInt .sapUiCalHeadB2,
.sapUiCalMulti .sapUiCalHeadB3,
.sapUiCalIntLarge .sapUiCalHeadB3,
.sapUiCalMonthInt .sapUiCalHeadB4 {
  width: 12.5rem;
}
.sapUiCalIntLarge .sapUiCalHeadB1 {
  width: auto;
  width: 12.5rem;
}
.sapUiCalTwoMonthsTwoColumns .sapUiCalHeadB1,
.sapUiCalTwoMonthsTwoColumns .sapUiCalHeadB2,
.sapUiCalTwoMonthsTwoColumns .sapUiCalHeadB3,
.sapUiCalTwoMonthsTwoColumns .sapUiCalHeadB4 {
  width: 5.75rem;
}
.sapUiCalTwoMonthsTwoColumns .sapUiCalHeadB1 {
  margin-left: calc(25% - 5.75rem);
}
.sapUiCalTwoMonthsTwoColumns .sapUiCalHeadB3 {
  margin-left: calc(50% - 2 * 5.75rem);
}
.sapUiCalItemNow > .sapUiCalItemText {
  border: 0.125rem solid #c0399f;
}
.sapUiCalContent > .sapUiCalMonthPicker,
.sapUiCalContent > .sapUiCalYearPicker {
  top: 0;
  padding: 2rem 0.5rem 1rem 0.5rem;
  background-color: #fff;
  left: 50%;
  margin-left: -9.5rem;
  /* to position in the middle (for multi-month) */
}
.sapUiCalInt > .sapUiCalContent > .sapUiCalMonthPicker,
.sapUiCalInt > .sapUiCalContent > .sapUiCalYearPicker {
  margin-left: 0;
  left: 0;
  padding-bottom: 0;
}
.sapUiCalTimeInt > .sapUiCalContent > .sapUiCalDatesRow {
  top: 0;
  padding: 0 0.5rem 0 0.5rem;
  margin: 0;
