/* ================================= */
/* CSS for control sap.m/IconTabBar  */
/* Belize theme                      */
/* ================================= */
/* Head Container */
.sapMITH {
  box-shadow: inset 0 -0.25rem 0 -0.125rem #969696;
  background-color: #fff;
  border-bottom: 0.125rem solid #fff;
}
/* Arrow */
.sapMITBSelected > .sapMITBContentArrow {
  border-bottom-color: #0961b9;
}
.sapMITBSelected.sapMITBFilterNeutral > .sapMITBContentArrow {
  border-bottom-color: #6a6d70;
}
.sapMITBSelected.sapMITBFilterPositive > .sapMITBContentArrow {
  border-bottom-color: #107e3e;
}
.sapMITBSelected.sapMITBFilterNegative > .sapMITBContentArrow {
  border-bottom-color: #bb0000;
}
.sapMITBSelected.sapMITBFilterCritical > .sapMITBContentArrow {
  border-bottom-color: #e9730c;
}
/* Content Container */
.sapMITBContent {
  border-bottom: 0.125rem solid #969696;
}
.sapMITB.sapMITBNoContentPadding > .sapMITBContainerContent,
.sapMITB.sapUiNoContentPadding > .sapMITBContainerContent {
  border-bottom: 0;
  border-top: 0;
}
/* Tab Filter Item */
.sapMITBTextOnly .sapMITBFilterDefault:hover > .sapMITBText {
  color: #1a1c1f;
}
.sapMITBTextOnly .sapMITBFilterDefault.sapMITBSelected > .sapMITBText {
  color: #0961b9;
  text-shadow: 0 0 0.125rem #fff;
}
.sapMITBTextOnly .sapMITBFilterDefault.sapMITBSelected:hover > .sapMITBText {
  color: #0854a0;
}
.sapMITBTextOnly .sapMITBFilterNeutral > .sapMITBText {
  color: #6a6d70;
}
.sapMITBTextOnly .sapMITBFilterPositive > .sapMITBText {
  color: #107e3e;
}
.sapMITBTextOnly .sapMITBFilterPositive:hover > .sapMITBText {
  color: #0a5128;
}
.sapMITBTextOnly .sapMITBFilterNegative > .sapMITBText {
  color: #bb0000;
}
.sapMITBTextOnly .sapMITBFilterNegative:hover > .sapMITBText {
  color: #880000;
}
.sapMITBTextOnly .sapMITBFilterCritical > .sapMITBText {
  color: #e9730c;
}
.sapMITBTextOnly .sapMITBFilterCritical:hover > .sapMITBText {
  color: #b85b0a;
}
.sapMITBFilterIcon {
  border: 1px solid #0a6ed1;
}
.sapMITBFilterIcon::before {
  text-shadow: 0 0 0.125rem #fff;
}
.sapMITBSelected .sapMITBFilterIcon::before {
  text-shadow: none;
}
.sapMITBFilterDefault {
  color: #0a6ed1;
}
.sapMITBFilterNeutral {
  border-color: #6a6d70;
  color: #6a6d70;
}
.sapMITBFilterPositive {
  border-color: #107e3e;
  color: #107e3e;
}
.sapMITBFilterNegative {
  border-color: #bb0000;
  color: #bb0000;
}
.sapMITBFilterCritical {
  border-color: #e9730c;
  color: #e9730c;
}
.sapMITBSelected .sapMITBFilterDefault {
  background: #0961b9;
  color: #fff;
}
.sapMITBSelected .sapMITBFilterNeutral {
  background: #6a6d70;
  color: #fff;
}
.sapMITBSelected .sapMITBFilterPositive {
  background: #107e3e;
  color: #fff;
}
.sapMITBSelected .sapMITBFilterNegative {
  background: #bb0000;
  color: #fff;
}
.sapMITBSelected .sapMITBFilterCritical {
  background: #e9730c;
  color: #fff;
}
/* Tab Separator Item */
[data-sap-ui-wc-root].sap-desktop .sapMITBArrowScroll:hover,
[data-sap-ui-wc-root].sap-desktop .sapMITBArrowScroll:active {
  color: #0a6ed1;
}
/* Custom focus outline */
[data-sap-ui-wc-root].sap-desktop .sapMITBItem:not(.sapUiDnDDragging):focus .sapMITBFilterIcon::after,
[data-sap-ui-wc-root].sap-desktop .sapMITBTextOnly .sapMITBItem:not(.sapUiDnDDragging):focus .sapMITBText::after,
[data-sap-ui-wc-root].sap-desktop .sapMITBItem:not(.sapUiDnDDragging):focus.sapMITBAll::after {
  content: "";
  position: absolute;
  top: -1px;
  bottom: -1px;
  right: -1px;
  left: -1px;
  border: 1px dotted #000;
  pointer-events: none;
}
[data-sap-ui-wc-root].sap-desktop .sapMITBItem:not(.sapUiDnDDragging):focus.sapMITBAll::after {
  bottom: 1.75rem;
}
[data-sap-ui-wc-root].sap-desktop .sapMITH.sapUiSizeCompact .sapMITBItem:focus.sapMITBAll::after {
  bottom: 1.5rem;
}
[data-sap-ui-wc-root].sap-desktop .sapMITH.sapUiSizeCompact .sapMITBTextOnly .sapMITBItem:focus .sapMITBText::after {
  top: 0.25rem;
}
[data-sap-ui-wc-root][data-sap-ui-browser^="ie"].sap-desktop .sapMITBItem:focus .sapMITBFilterIcon::after,
[data-sap-ui-wc-root][data-sap-ui-browser^="ed"].sap-desktop .sapMITBItem:focus .sapMITBFilterIcon::after,
[data-sap-ui-wc-root][data-sap-ui-browser^="ie"].sap-desktop .sapMITBTextOnly .sapMITBItem:focus .sapMITBText::after,
[data-sap-ui-wc-root][data-sap-ui-browser^="ed"].sap-desktop .sapMITBTextOnly .sapMITBItem:focus .sapMITBText::after,
[data-sap-ui-wc-root][data-sap-ui-browser^="ie"].sap-desktop .sapMITBItem:focus.sapMITBAll::after,
[data-sap-ui-wc-root][data-sap-ui-browser^="ed"].sap-desktop .sapMITBItem:focus.sapMITBAll::after {
  border: 1px dashed #000;
}
/* container background design */
.sapMITBBackgroundDesignTranslucent > .sapMITBContainerContent > .sapMITBContent {
  background-color: rgba(255, 255, 255, 0.6);
}
/* header background design */
